mapper.insert:

	select seq_notice.nextval from dual			

insert into free(
	bono, botitle, bocontent, bowriter, bodate
)values(
	#{boNo}, #{boTitle}, #{boContent}, #{boWriter} ,sysdate
)

mapper.select:
select b.*
from(
	select
		a.*, row_number() over (order by a.bono desc)rnum
	from(
		select bono, botitle, bocontent, bowriter, to_char(bodate, 'yy-mm-dd hh24:mi:ss')bodate, bohit
		from free
		where 1 = 1

		order by bono desc
	)a
)b

	where b.rnum >= #{startRow} and b.rnum <= #{endRow}
