mapper.select:

select b.*
from(
    select a.*, row_number() over (order by a.bono desc) rnum
    from(
        select 
            bono, botitle, bocontent, bowriter, bohit 
        from board
        where 1=1 
        order by bono desc
        )a
    )b
where rnum <= 5


select b.*
from(
    select a.*, row_number() over (order by a.bono desc) rnum
    from(
        select 
            bono, botitle, bocontent, bowriter, bohit 
        from notice
        where 1=1 
        order by bono desc
        )a
    )b
where rnum <= 5


select b.*
from(
    select a.*, row_number() over (order by a.bono desc) rnum
    from(
        select 
            bono, botitle, bocontent, bowriter, bohit 
        from free
        where 1=1 
        order by bono desc
        )a
    )b
where rnum <= 5
